package New;

public class IfElse1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 30;
		int y = 100;
		int z = 0;
		z = x+y;
		
		if(x<100 && y == 90) {
			System.out.println("x is less than 100");
				}
		
		else if (z>100) {
			System.out.println("Else if statement");
				
		}
		
		else {
			System.out.println("This is else");
		}

	}

}
